// –ü–û–ò–°–ö–û–í–ò–ö –§–ò–õ–¨–ú–û–í
const searchBtn = document.getElementById("search-btn");
const searchBox = document.getElementById("searchbox");
const resultsDiv = document.getElementById("results");

function highlight(text, term) {
  const regex = new RegExp(`(${term})`, "gi");
  return text.replace(regex, "<mark>$1</mark>");
}

function search() {
  const term = searchBox.value.trim();
  resultsDiv.innerHTML = "";

  if (!term) {
    resultsDiv.innerHTML = "<p class='no-results'>–í–≤–µ–¥—ñ—Ç—å –Ω–∞–∑–≤—É —Ñ—ñ–ª—å–º—É.</p>";
    return;
  }

  const results = data.filter(item =>
    item.title.toLowerCase().includes(term.toLowerCase()) ||
    item.description.toLowerCase().includes(term.toLowerCase())
  );

  if (results.length === 0) {
    resultsDiv.innerHTML = "<p class='no-results'>–§—ñ–ª—å–º–∏ –Ω–µ –∑–Ω–∞–π–¥–µ–Ω—ñ üòï</p>";
    return;
  }

  let html = "<h2>–†–µ–∑—É–ª—å—Ç–∞—Ç–∏ –ø–æ—à—É–∫—É:</h2>";
  results.forEach(item => {
    html += `
      <div class="result">
        <a href="${item.url}">${highlight(item.title, term)}</a>
        <p>${highlight(item.description, term)}</p>
        <p><strong>–†—ñ–∫:</strong> ${item.year}</p>
      </div>
    `;
  });
  resultsDiv.innerHTML = html;
}

// –°–ú–ï–ù–ê –¢–ï–ú–´ –í –ú–ï–ù–Æ –•–ï–î–ï–†–ê
searchBtn.addEventListener("click", search);
searchBox.addEventListener("keydown", e => {
  if (e.key === "Enter") search();
});

document.addEventListener("DOMContentLoaded", () => {
  const toggleBtn = document.getElementById("theme-toggle");
  const menu = document.getElementById("theme-menu");

  if (!toggleBtn) return;

  toggleBtn.addEventListener("click", () => {
    if (menu) {
      menu.hidden = !menu.hidden;
    }
  });

  const darkBtn = document.getElementById("theme-toggle-dark");
  const lightBtn = document.getElementById("theme-toggle-light");

  darkBtn?.addEventListener("click", () => {
    document.body.classList.add("dark-theme");
    document.body.classList.remove("light-theme");
    if (menu) menu.hidden = true;
  });

  lightBtn?.addEventListener("click", () => {
    document.body.classList.add("light-theme");
    document.body.classList.remove("dark-theme");
    if (menu) menu.hidden = true;
  });
});

// –ö–ê–¢–ê–õ–û–ì –§–ò–õ–¨–ú–û–í –í –ù–ê–í–ë–ê–†–ï
document.addEventListener("DOMContentLoaded", () => {
  const catalogBtn = document.getElementById("catalog-btn");
  const catalogDiv = document.getElementById("catalog");

  if (!catalogBtn || !catalogDiv) return;

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –æ–∫–Ω–∞ –∫–∞—Ç–∞–ª–æ–≥–∞
  catalogBtn.addEventListener("click", (e) => {
    e.stopPropagation(); // —á—Ç–æ–±—ã –∫–ª–∏–∫ –Ω–µ –∑–∞–∫—Ä—ã–ª —Å—Ä–∞–∑—É
    catalogDiv.classList.toggle("show");
  });

  // –ö–ª–∏–∫ –≤–Ω–µ ‚Äî –∑–∞–∫—Ä—ã—Ç—å
  document.addEventListener("click", (e) => {
    if (!catalogDiv.contains(e.target) && !catalogBtn.contains(e.target)) {
      catalogDiv.classList.remove("show");
    }
  });

  // –ó–∞–∫—Ä—ã—Ç—å –ø–æ Esc
  document.addEventListener("keydown", (e) => {
    if (e.key === "Escape") catalogDiv.classList.remove("show");
  });
});